        return new Source("{% extends 'base.html.twig' %}

{% block title %}Video{% endblock %}

{% block body %}
    <h1>Video</h1>

    <table class=\"table\">
        <tbody>
            <tr>
                <th>Id</th>
                <td>{{ video.id }}</td>
            </tr>
            <tr>
                <th>Name</th>
                <td>{{ video.name }}</td>
            </tr>
            <tr>
                <th>Description</th>
                <td>{{ video.description }}</td>
            </tr>
            <tr>
                <th>Created_at</th>
                <td>{{ video.createdAt ? video.createdAt|date('Y-m-d H:i:s') : '' }}</td>
            </tr>
            <tr>
                <th>User</th>
                <td>{{ video.user.pseudo }}</td>
            </tr>
            <tr>
                <th>Link</th>
                <td>{{ video.link }}</td>
            </tr>
        </tbody>
    </table>

    <a href=\"{{ path('video_index') }}\">back to list</a>

    <a href=\"{{ path('video_edit', {'id': video.id}) }}\">edit</a>

    <iframe width=\"420\" height=\"315\"
            src=\"{{ video.link }}\">
    </iframe>
    {{ include('video/_delete_form.html.twig') }}

    {{ include('comment/_form.html.twig', {'button_label': 'Update'}) }}

{% endblock %}
